.net/license/epl-v10.html\r\n */\r\npackage sample;\r\n\r\n\r\nimport robocode.DeathEvent;\r\nimport robocode.Robot;\r\nimport robocode.ScannedRobotEvent;\r\nimport static robocode.util.Utils.normalRelativeAngleDegrees;\r\n\r\nimport java.awt.*;\r\n\r\n\r\n/**\r\n * Corners - a sample robot by Mathew Nelson.\r\n * \r\n * This robot moves to a corner, then swings the gun back and forth.\r\n * If it dies, it tries a new corner in the next round.\r\n *\r\n * @author Mathew A. Nelson (original)\r\n * @author Flemming N. Larsen (contributor)\r\n */\r\npublic class Corners extends Robot {\r\n	int others; // Number of other robots in the game\r\n	static int corner = 0; // Which corner we are currently using\r\n	// static so that it keeps it between rounds.\r\n	boolean stopWhenSeeRobot = false; // See goCorner()\r\n\r\n	/**\r\n	 * run:  Corners\' main run function.\r\n	 */\r\n	public void run() {\r\n		// Set colors\r\n		setBodyColor(Color.red);\r\n		setGunColor(Color.black);\r\n		setRadarColor(Color.yellow);\r\n		setBulletColor(Color.green);\r\n		setScanColor(Color.green);\r\n\r\n		// Save # of other bots\r\n		others = getOthers();\r\n\r\n		// Move to a corner\r\n		goCorner();\r\n\r\n		// Initialize gun turn speed to 3\r\n		int gunIncrement = 3;\r\n\r\n		// Spin gun back and forth\r\n		while (true) {\r\n			for (int i = 0; i < 30; i++) {\r\n				turnGunLeft(gunIncrement);\r\n			}\r\n			gunIncrement *= -1;\r\n		}\r\n	}\r\n\r\n	/**\r\n	 * goCorner:  A very inefficient way to get to a corner.  Can you do better?\r\n	 */\r\n	public void goCorner() {\r\n		// We don\'t want to stop when we\'re just turning...\r\n		stopWhenSeeRobot = false;\r\n		// turn to face the wall to the \"right\" of our desired corner.\r\n		turnRight(normalRelativeAngleDegrees(corner - getHeading()));\r\n		// Ok, now we don\'t want to crash into any robot in our way...\r\n		stopWhenSeeRobot = true;\r\n		// Move to that wall\r\n		ahead(5000);\r\n		// Turn to face the corner\r\n		turnLeft(90);\r\n		// Move to the corner\r\n		ahead(5000);\r\n		// Turn gun to starting point\r\n		turnGunLeft(90);\r\n	}\r\n\r\n	/**\r\n	 * onScannedRobot:  Stop and fire!\r\n	 */\r\n	public void onScannedRobot(ScannedRobotEvent e) {\r\n		// Should we stop, or just fire?\r\n		if (stopWhenSeeRobot) {\r\n			// Stop everything!  You can safely call stop multiple times.\r\n			stop();\r\n			// Call our custom firing method\r\n			smartFire(e.getDistance());\r\n			// Look for another robot.\r\n			// NOTE:  If you call scan() inside onScannedRobot, and it sees a robot,\r\n			// the game will interrupt the event handler and start it over\r\n			scan();\r\n			// We won\'t get here if we saw another robot.\r\n			// Okay, we didn\'t see another robot... start moving or turning again.\r\n			resume();\r\n		} else {\r\n			smartFire(e.getDistance());\r\n		}\r\n	}\r\n\r\n	/**\r\n	 * smartFire:  Custom fire method that determines firepower based on distance.\r\n	 *\r\n	 * @param robotDistance the distance to the robot to fire at\r\n	 */\r\n	public void smartFire(double robotDistance) {\r\n		if (robotDistance > 200 || getEnergy() < 15) {\r\n			fire(1);\r\n		} else if (robotDistance > 50) {\r\n			fire(2);\r\n		} else {\r\n			fire(3);\r\n		}\r\n	}\r\n\r\n	/**\r\n	 * onDeath:  We died.  Decide whether to try a different corner next game.\r\n	 */\r\n	public void onDeath(DeathEvent e) {\r\n		// Well, others should never be 0, but better safe than sorry.\r\n		if (others == 0) {\r\n			return;\r\n		}\r\n\r\n		// If 75% of the robots are still alive when we die, we\'ll switch corners.\r\n		if ((others - getOthers()) / (double) others < .75) {\r\n			corner += 90;\r\n			if (corner == 270) {\r\n				corner = -90;\r\n			}\r\n			out.println(\"I died and did poorly... switching corner to \" + corner);\r\n		} else {\r\n			out.println(\"I died but did well.  I will still use corner \" + corner);\r\n		}\r\n	}\r\n}\r\n\r\n\r\n \r\n    \r\n\r\n \r\n    \r\n',X'',0),
	(3,'User','sample','Fire','Y','sample/Fire.java','','Wed Feb 03 10:05:48 CST 2016',' /**\r\n * Copyright (c) 2001-2014 Mathew A. Nelson and Robocode contributors\r\n * All rights reserved. This program and the accompanying materials\r\n * are made available under the terms of the Eclipse Public License v1.0\r\n * which accompanies this distribution, and is available at\r\n * http://robocode.sourceforge.net/license/epl-v10.html\r\n */\r\npackage sample;\r\n\r\n\r\nimport robocode.HitByBulletEvent;\r\nimport robocode.HitRobotEvent;\r\nimport robocode.Robot;\r\nimport robocode.ScannedRobotEvent;\r\nimport static robocode.util.Utils.normalRelativeAngleDegrees;\r\n\r\nimport java.awt.*;\r\n\r\n\r\n/**\r\n * Fire - a sample robot by Mathew Nelson, and maintained.\r\n * \r\n * Sits still. Spins gun around. Moves when hit.\r\n *\r\n * @author Mathew A. Nelson (original)\r\n * @author Flemming N. Larsen (contributor)\r\n */\r\npublic class Fire extends Robot {\r\n	int dist = 50; // distance to move when we\'re hit\r\n\r\n	/**\r\n	 * run:  Fire\'s main run function\r\n	 */\r\n	public void run() {\r\n		// Set colors\r\n		setBodyColor(Color.orange);\r\n		setGunColor(Color.orange);\r\n		setRadarColor(Color.red);\r\n		setScanColor(Color.red);\r\n		setBulletColor(Color.red);\r\n\r\n		// Spin the gun around slowly... forever\r\n		while (true) {\r\n			turnGunRight(5);\r\n		}\r\n	}\r\n\r\n	/**\r\n	 * onScannedRobot:  Fire!\r\n	 */\r\n	public void onScannedRobot(ScannedRobotEvent e) {\r\n		// If the other robot is close by, and we have plenty of life,\r\n		// fire hard!\r\n		if (e.getDistance() < 50 && getEnergy() > 50) {\r\n			fire(3);\r\n		} // otherwise, fire 1.\r\n		else {\r\n			fire(1);\r\n		}\r\n		// Call scan again, before we turn the gun\r\n		scan();\r\n	}\r\n\r\n	/**\r\n	 * onHitByBullet:  Turn perpendicular to the bullet, and move a bit.\r\n	 */\r\n	public void onHitByBullet(HitByBulletEvent e) {\r\n		turnRight(normalRelativeAngleDegrees(90 - (getHeading() - e.getHeading())));\r\n\r\n		ahead(dist);\r\n		dist *= -1;\r\n		scan();\r\n	}\r\n\r\n	/**\r\n	 * onHitRobot:  Aim at it.  Fire Hard!\r\n	 */\r\n	public void onHitRobot(HitRobotEvent e) {\r\n		double turnGunAmt = normalRelativeAngleDegrees(e.getBearing() + getHeading() - getGunHeading());\r\n\r\n		turnGunRight(turnGunAmt);\r\n		fire(3);\r\n	}\r\n}\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n ',X'',0),
	(4,'User','sample','Interactive','Y','sample/Interactive.java','','Wed Feb 03 09:56:31 CST 2016','/**\r\n * Copyright (c) 2001-2014 Mathew A. Nelson and Robocode contributors\r\n * All rights reserved. This program and the accompanying materials\r\n * are made available under the terms of the Eclipse Public License v1.0\r\n * which accompanies this distribution, and is available at\r\n * http://robocode.sourceforge.net/license/epl-v10.html\r\n */\r\npackage sample;\r\n\r\n\r\nimport robocode.AdvancedRobot;\r\nimport static robocode.util.Utils.normalAbsoluteAngle;\r\nimport static robocode.util.Utils.normalRelativeAngle;\r\n\r\nimport java.awt.*;\r\nimport java.awt.event.KeyEvent;\r\nimport static java.awt.event.KeyEvent.*;\r\nimport java.awt.event.MouseEvent;\r\nimport java.awt.event.MouseWheelEvent;\r\n\r\n\r\n/**\r\n * Interactive - a sample robot by Flemming N. Larsen.\r\n * <p/>\r\n * This is a robot that is controlled using the arrow keys and mouse only.\r\n * <p/>\r\n * Keys:\r\n * - W or arrow up:    Move forward\r\n * - S or arrow down:  Move backward\r\n * - A or arrow right: Turn right\r\n * - D or arrow left:  Turn left\r\n * Mouse:\r\n * - Moving:      Moves the aim, which the gun will follow\r\n * - Wheel up:    Move forward\r\n * - Wheel down:  Move backward\r\n * - Button 1:    Fire a bullet with power = 1\r\n * - Button 2:    Fire a bullet with power = 2\r\n * - Button 3:    Fire a bullet with power = 3\r\n * <p/>\r\n * The bullet color depends on the fire power:\r\n * - Power = 1:   Yellow\r\n * - Power = 2:   Orange\r\n * - Power = 3:   Red\r\n * <p/>\r\n * Note that the robot will continue firing as long as the mouse button is\r\n * pressed down.\r\n * <p/>\r\n * By enabling the \"Paint\" button on the robot console window for this robot,\r\n * a cross hair will be painted for the robots current aim (controlled by the\r\n * mouse).\r\n *\r\n * @author Flemming N. Larsen (original)\r\n *\r\n * @version 1.2\r\n *\r\n * @since 1.3.4\r\n */\r\npublic class Interactive extends AdvancedRobot {\r\n\r\n	// Move direction: 1 = move forward, 0 = stand still, -1 = move backward\r\n	int moveDirection;\r\n\r\n	// Turn direction: 1 = turn right, 0 = no turning, -1 = turn left\r\n	int turnDirection;\r\n\r\n	// Amount of pixels/units to move\r\n	double moveAmount;\r\n\r\n	// The coordinate of the aim (x,y)\r\n	int aimX, aimY;\r\n\r\n	// Fire power, where 0 = don\'t fire\r\n	int firePower;\r\n\r\n	// Called when the robot must run\r\n	public void run() {\r\n\r\n		// Sets the colors of the robot\r\n		// body = black, gun = white, radar = red\r\n		setColors(Color.BLACK, Color.WHITE, Color.RED);\r\n\r\n		// Loop forever\r\n		for (;;) {\r\n			// Sets the robot to move forward, backward or stop moving depending\r\n			// on the move direction and amount of pixels to move\r\n			setAhead(moveAmount * moveDirection);\r\n\r\n			// Decrement the amount of pixels to move until we reach 0 pixels\r\n			// This way the robot will automatically stop if the mouse wheel\r\n			// has stopped it\'s rotation\r\n			moveAmount = Math.max(0, moveAmount - 1);\r\n\r\n			// Sets the robot to turn right or turn left (at maximum speed) or\r\n			// stop turning depending on the turn direction\r\n			setTurnRight(45 * turnDirection); // degrees\r\n\r\n			// Turns the gun toward the current aim coordinate (x,y) controlled by\r\n			// the current mouse coordinate\r\n			double angle = normalAbsoluteAngle(Math.atan2(aimX - getX(), aimY - getY()));\r\n\r\n			setTurnGunRightRadians(normalRelativeAngle(angle - getGunHeadingRadians()));\r\n\r\n			// Fire the gun with the specified fire power, unless the fire power = 0\r\n			if (firePower > 0) {\r\n				setFire(firePower);\r\n			}\r\n\r\n			// Execute all pending set-statements\r\n			execute();\r\n\r\n			// Next turn is processed in this loop..\r\n		}\r\n	}\r\n\r\n	// Called when a key has been pressed\r\n	public void onKeyPressed(KeyEvent e) {\r\n		switch (e.getKeyCode()) {\r\n		case VK_UP:\r\n		case VK_W:\r\n			// Arrow up key: move direction = forward (infinitely)\r\n			moveDirection = 1;\r\n			moveAmount = Double.POSITIVE_INFINITY;\r\n			break;\r\n\r\n		case VK_DOWN:\r\n		case VK_S:\r\n			// Arrow down key: move direction = backward (infinitely)\r\n			moveDirection = -1;\r\n			moveAmount = Double.POSITIVE_INFINITY;\r\n			break;\r\n\r\n		case VK_RIGHT:\r\n		case VK_D:\r\n			// Arrow right key: turn direction = right\r\n			turnDirection = 1;\r\n			break;\r\n\r\n		case VK_LEFT:\r\n		case VK_A:\r\n			// Arrow left key: turn direction = left\r\n			turnDirection = -1;\r\n			break;\r\n		}\r\n	}\r\n\r\n	// Called when a key has been released (after being pressed)\r\n	public void onKeyReleased(KeyEvent e) {\r\n		switch (e.getKeyCode()) {\r\n		case VK_UP:\r\n		case VK_W:\r\n		case VK_DOWN:\r\n		case VK_S:\r\n			// Arrow up and down keys: move direction = stand still\r\n			moveDirection = 0;\r\n			moveAmount = 0;\r\n			break;\r\n\r\n		case VK_RIGHT:\r\n		case VK_D:\r\n		case VK_LEFT:\r\n		case VK_A:\r\n			// Arrow right and left keys: turn direction = stop turning\r\n			turnDirection = 0;\r\n			break;\r\n		}\r\n	}\r\n\r\n	// Called when the mouse wheel is rotated\r\n	public void onMouseWheelMoved(MouseWheelEvent e) {\r\n		// If the wheel rotation is negative it means that it is moved forward.\r\n		// Set move direction = forward, if wheel is moved forward.\r\n		// Otherwise, set move direction = backward\r\n		moveDirection = (e.getWheelRotation() < 0) ? 1 : -1;\r\n\r\n		// Set the amount to move = absolute wheel rotation amount * 5 (speed)\r\n		// Here 5 means 5 pixels per wheel rotation step. The higher value, the\r\n		// more speed\r\n		moveAmount += Math.abs(e.getWheelRotation()) * 5;\r\n	}\r\n\r\n	// Called when the mouse has been moved\r\n	public void onMouseMoved(MouseEvent e) {\r\n		// Set the aim coordinate = the mouse pointer coordinate\r\n		aimX = e.getX();\r\n		aimY = e.getY();\r\n	}\r\n\r\n	// Called when a mouse button has been pressed\r\n	public void onMousePressed(MouseEvent e) {\r\n		if (e.getButton() == MouseEvent.BUTTON3) {\r\n			// Button 3: fire power = 3 energy points, bullet color = red\r\n			firePower = 3;\r\n			setBulletColor(Color.RED);\r\n		} else if (e.getButton() == MouseEvent.BUTTON2) {\r\n			// Button 2: fire power = 2 energy points, bullet color = orange\r\n			firePower = 2;\r\n			setBulletColor(Color.ORANGE);\r\n		} else {\r\n			// Button 1 or unknown button:\r\n			// fire power = 1 energy points, bullet color = yellow\r\n			firePower = 1;\r\n			setBulletColor(Color.YELLOW);\r\n		}\r\n	}\r\n\r\n	// Called when a mouse button has been released (after being pressed)\r\n	public void onMouseReleased(MouseEvent e) {\r\n		// Fire power = 0, which means \"don\'t fire\"\r\n		firePower = 0;\r\n	}\r\n\r\n	// Called in order to paint graphics for this robot.\r\n	// \"Paint\" button on the robot console window for this robot must be\r\n	// enabled in order to see the paintings.\r\n	public void onPaint(Graphics2D g) {\r\n		// Draw a red cross hair with the center at the current aim\r\n		// coordinate (x,y)\r\n		g.setColor(Color.RED);\r\n		g.drawOval(aimX - 15, aimY - 15, 30, 30);\r\n		g.drawLine(aimX, aimY - 4, aimX, aimY + 4);\r\n		g.drawLine(aimX - 4, aimY, aimX + 4, aimY);\r\n	}\r\n}\r\n\r\n',X'',0),
	(5,'User','sample','Interactive_v2','Y','sample/Interactive_v2.java','','Wed Feb 03 10:11:47 CST 2016','/**\r\n * Copyright (c) 2001-2014 Mathew A. Nelson and Robocode contributors\r\n * All rights reserved. This program and the accompanying materials\r\n * are made available under the terms of the Eclipse Public License v1.0\r\n * which accompanies this distribution, and is available at\r\n * http://robocode.sourceforge.net/license/epl-v10.html\r\n */\r\npackage sample;\r\n\r\n\r\nimport robocode.AdvancedRobot;\r\nimport robocode.util.Utils;\r\nimport static robocode.util.Utils.normalAbsoluteAngle;\r\nimport static robocode.util.Utils.normalRelativeAngle;\r\n\r\nimport java.awt.*;\r\nimport java.awt.event.KeyEvent;\r\nimport static java.awt.event.KeyEvent.*;\r\nimport java.awt.event.MouseEvent;\r\nimport java.awt.event.MouseWheelEvent;\r\nimport java.util.HashSet;\r\nimport java.util.Set;\r\n\r\n\r\n/**\r\n * Interactive_v2 - a modified version of the sample robot Interactive by Flemming N. Larsen\r\n *                  to use absolute movements (up, right, down, left) by Tuan Anh Nguyen.\r\n * <p/>\r\n * This is a robot that is controlled using the arrow keys (or WASD) and mouse only.\r\n * <p/>\r\n * Keys:\r\n * - W or arrow up:    Move up\r\n * - S or arrow down:  Move down\r\n * - A or arrow right: Move right\r\n * - D or arrow left:  Move left\r\n * Mouse:\r\n * - Moving:      Moves the aim, which the gun will follow\r\n * - Button 1:    Fire a bullet with power = 1\r\n * - Button 2:    Fire a bullet with power = 2\r\n * - Button 3:    Fire a bullet with power = 3\r\n * <p/>\r\n * The bullet color depends on the fire power:\r\n * - Power = 1:   Yellow\r\n * - Power = 2:   Orange\r\n * - Power = 3:   Red\r\n * <p/>\r\n * Note that the robot will continue firing as long as the mouse button is\r\n * pressed down.\r\n * <p/>\r\n * By enabling the \"Paint\" button on the robot console window for this robot,\r\n * a cross hair will be painted for the robots current aim (controlled by the\r\n * mouse).\r\n *\r\n * @author Flemming N. Larsen (original)\r\n * @author Tuan Anh Nguyen (contributor)\r\n *\r\n * @version 1.0\r\n *\r\n * @since 1.7.2.2\r\n */\r\npublic class Interactive_v2 extends AdvancedRobot {\r\n\r\n	// The coordinate of the aim (x,y)\r\n	int aimX, aimY;\r\n\r\n	// Fire power, where 0 = don\'t fire\r\n	int firePower;\r\n\r\n	// Absolute directions on the screen\r\n	private enum Direction {\r\n		UP,\r\n		DOWN,\r\n		LEFT,\r\n		RIGHT\r\n	}\r\n\r\n	// Current move directions\r\n	private final Set<Direction> directions = new HashSet<Direction>();\r\n\r\n	// Called when the robot must run\r\n	public void run() {\r\n\r\n		// Sets the colors of the robot\r\n		// body = black, gun = white, radar = red\r\n		setColors(Color.BLACK, Color.WHITE, Color.RED);\r\n\r\n		// Loop forever\r\n		for (;;) {\r\n			// Move the robot compared to the distance to move\r\n			setAhead(distanceToMove());\r\n\r\n			// Turn the body to it points in the correct direction\r\n			setTurnRight(angleToTurnInDegrees());\r\n\r\n			// Turns the gun toward the current aim coordinate (x,y) controlled by\r\n			// the current mouse coordinate\r\n			double angle = normalAbsoluteAngle(Math.atan2(aimX - getX(), aimY - getY()));\r\n\r\n			setTurnGunRightRadians(normalRelativeAngle(angle - getGunHeadingRadians()));\r\n\r\n			// Fire the gun with the specified fire power, unless the fire power = 0\r\n			if (firePower > 0) {\r\n				setFire(firePower);\r\n			}\r\n\r\n			// Execute all pending set-statements\r\n			execute();\r\n\r\n			// Next turn is processed in this loop..\r\n		}\r\n	}\r\n\r\n	// Called when a key has been pressed\r\n	public void onKeyPressed(KeyEvent e) {\r\n		switch (e.getKeyCode()) {\r\n		case VK_UP:\r\n		case VK_W:\r\n			directions.add(Direction.UP);\r\n			break;\r\n\r\n		case VK_DOWN:\r\n		case VK_S:\r\n			directions.add(Direction.DOWN);\r\n			break;\r\n\r\n		case VK_RIGHT:\r\n		case VK_D:\r\n			directions.add(Direction.RIGHT);\r\n			break;\r\n\r\n		case VK_LEFT:\r\n		case VK_A:\r\n			directions.add(Direction.LEFT);\r\n			break;\r\n		}\r\n	}\r\n\r\n	// Called when a key has been released (after being pressed)\r\n	public void onKeyReleased(KeyEvent e) {\r\n		switch (e.getKeyCode()) {\r\n		case VK_UP:\r\n		case VK_W:\r\n			directions.remove(Direction.UP);\r\n			break;\r\n\r\n		case VK_DOWN:\r\n		case VK_S:\r\n			directions.remove(Direction.DOWN);\r\n			break;\r\n\r\n		case VK_RIGHT:\r\n		case VK_D:\r\n			directions.remove(Direction.RIGHT);\r\n			break;\r\n\r\n		case VK_LEFT:\r\n		case VK_A:\r\n			directions.remove(Direction.LEFT);\r\n			break;\r\n		}\r\n	}\r\n\r\n	// Called when the mouse wheel is rotated\r\n	public void onMouseWheelMoved(MouseWheelEvent e) {// Do nothing\r\n	}\r\n\r\n	// Called when the mouse has been moved\r\n	public void onMouseMoved(MouseEvent e) {\r\n		// Set the aim coordinate = the mouse pointer coordinate\r\n		aimX = e.getX();\r\n		aimY = e.getY();\r\n	}\r\n\r\n	// Called when a mouse button has been pressed\r\n	public void onMousePressed(MouseEvent e) {\r\n		if (e.getButton() == MouseEvent.BUTTON3) {\r\n			// Button 3: fire power = 3 energy points, bullet color = red\r\n			firePower = 3;\r\n			setBulletColor(Color.RED);\r\n		} else if (e.getButton() == MouseEvent.BUTTON2) {\r\n			// Button 2: fire power = 2 energy points, bullet color = orange\r\n			firePower = 2;\r\n			setBulletColor(Color.ORANGE);\r\n		} else {\r\n			// Button 1 or unknown button:\r\n			// fire power = 1 energy points, bullet color = yellow\r\n			firePower = 1;\r\n			setBulletColor(Color.YELLOW);\r\n		}\r\n	}\r\n\r\n	// Called when a mouse button has been released (after being pressed)\r\n	public void onMouseReleased(MouseEvent e) {\r\n		// Fire power = 0, which means \"don\'t fire\"\r\n		firePower = 0;\r\n	}\r\n\r\n	// Called in order to paint graphics for this robot.\r\n	// \"Paint\" button on the robot console window for this robot must be\r\n	// enabled in order to see the paintings.\r\n	public void onPaint(Graphics2D g) {\r\n		// Draw a red cross hair with the center at the current aim\r\n		// coordinate (x,y)\r\n		g.setColor(Color.RED);\r\n		g.drawOval(aimX - 15, aimY - 15, 30, 30);\r\n		g.drawLine(aimX, aimY - 4, aimX, aimY   4);\r\n		g.drawLine(aimX - 4, aimY, aimX   4, aimY);\r\n	}\r\n\r\n	// Returns the angle to turn, which is the delta between the desired\r\n	// direction and the current heading of the robot\r\n	private double angleToTurnInDegrees() {\r\n		if (directions.isEmpty()) {\r\n			return 0;\r\n		}\r\n		return Utils.normalRelativeAngleDegrees(desiredDirection() - getHeading());\r\n	}\r\n\r\n	// Returns the distance to move\r\n	private double distanceToMove() {\r\n		// If no keys are pressed, we should not move at all\r\n		if (directions.isEmpty()) {\r\n			return 0;\r\n		}\r\n		// If the robot has more than 45 degrees to turn, move only 5 pixel \r\n		if (Math.abs(angleToTurnInDegrees()) > 45) {\r\n			return 5;\r\n		}\r\n		// Otherwise, move at full speed\r\n		return Double.POSITIVE_INFINITY;\r\n	}\r\n\r\n	// Return the desired direction depending on the pending move directions.\r\n	// With one arrow key pressed, the move to N, E, S, W.\r\n	// With two keys pressed, the robot also move to NE, NW, SE, SW.\r\n	private double desiredDirection() {\r\n		if (directions.contains(Direction.UP)) {\r\n			if (directions.contains(Direction.RIGHT)) {\r\n				return 45;\r\n			}\r\n			if (directions.contains(Direction.LEFT)) {\r\n				return 315;\r\n			}\r\n			return 0;\r\n		}\r\n		if (directions.contains(Direction.DOWN)) {\r\n			if (directions.contains(Direction.RIGHT)) {\r\n				return 135;\r\n			}\r\n			if (directions.contains(Direction.LEFT)) {\r\n				return 225;\r\n			}\r\n			return 180;\r\n		}\r\n		if (directions.contains(Direction.RIGHT)) {\r\n			return 90;\r\n		}\r\n		if (directions.contains(Direction.LEFT)) {\r\n			return 270;\r\n		}\r\n		return 0;\r\n	}\r\n}\r\n\r\n',X'',0),
	(6,'User','sample','MyFirstJuniorRobot','Y','sample/MyFirstJuniorRobot.java','','Mon Jan 25 10:03:29 CST 2016','/**\n * Copyright (c) 2001-2014 Mathew A. Nelson and Robocode contributors\n * All rights reserved. This program and the accompanying materials\n * are made available under the terms of the Eclipse Public License v1.0\n * which accompanies this distribution, and is available at\n * http://robocode.sourceforge.net/license/epl-v10.html\n */\npackage sample;\n\n\nimport robocode.JuniorRobot;\n\n\n/**\n * MyFirstJuniorRobot - a sample robot by Flemming N. Larsen\n * <p/>\n * Moves in a seesaw motion, and spins the gun around at each end\n * when it cannot see any enemy robot. When the robot sees and enemy\n * robot, it will immediately turn the gun and fire at it.\n *\n * @author Flemming N. Larsen (original)\n */\npublic class MyFirstJuniorRobot extends JuniorRobot {\n\n	/**\n	 * MyFirstJuniorRobot\'s run method - Seesaw as default\n	 */\n	public void run() {\n		// Set robot colors\n		setColors(green, black, blue);\n\n		// Seesaw forever\n		while (true) {\n			ahead(100); // Move ahead 100\n			turnGunRight(360); // Spin gun around\n			back(100); // Move back 100\n			turnGunRight(360); // Spin gun around\n		}\n	}\n\n	/**\n	 * When we see a robot, turn the gun towards it and fire\n	 */\n	public void onScannedRobot() {\n		// Turn gun to point at the scanned robot\n		turnGunTo(scannedAngle);\n\n		// Fire!\n		fire(1);\n	}\n\n	/**\n	 * We were hit!  Turn and move perpendicular to the bullet,\n	 * so our seesaw might avoid a future shot.\n	 */\n	public void onHitByBullet() {\n		// Move ahead 100 and in the same time turn left papendicular to the bullet\n		turnAheadLeft(100, 90 - hitByBulletBearing);\n	}\n}\n',X'',0),
	(7,'User','sample','MyFirstRobot','Y','sample/MyFirstRobot.java','','Tue Jan 26 11:27:43 CST 2016','/**\n * Copyright (c) 2001-2014 Mathew A. Nelson and Robocode contributors\n * All rights reserved. This program and the accompanying materials\n * are made available under the terms of the Eclipse Public License v1.0\n * which accompanies this distribution, and is available at\n * http://robocode.sourceforge.net/license/epl-v10.html\n */\npackage sample;\n\n\nimport robocode.HitByBulletEvent;\nimport robocode.Robot;\nimport robocode.ScannedRobotEvent;\n\n\n/**\n * MyFirstRobot - a sample robot by Mathew Nelson.\n * <p/>\n * Moves in a seesaw motion, and spins the gun around at each end.\n *\n * @author Mathew A. Nelson (original)\n */\npublic class MyFirstRobot extends Robot {\n\n	/**\n	 * MyFirstRobot\'s run method - Seesaw\n	 */\n	public void run() {\n\n		while (true) {\n			ahead(100); // Move ahead 100\n			turnGunRight(360); // Spin gun around\n			back(100); // Move back 100\n			turnGunRight(360); // Spin gun around\n		}\n	}\n\n	/**\n	 * Fire when we see a robot\n	 */\n	public void onScannedRobot(ScannedRobotEvent e) {\n		fire(1);\n	}\n\n	/**\n	 * We were hit!  Turn perpendicular to the bullet,\n	 * so our seesaw might avoid a future shot.\n	 */\n	public void onHitByBullet(HitByBulletEvent e) {\n		turnLeft(90 - e.getBearing());\n	}\n}\n\n\n\n',X'',0),
	(8,'User','sample','PaintingRobot','Y','C:/robocode/robots/sample/PaintingRobot.java','','Fri Jan 22 17:33:56 CST 2016','/**\n * Copyright (c) 2001-2014 Mathew A. Nelson and Robocode contributors\n * All rights reserved. This program and the accompanying materials\n * are made available under the terms of the Eclipse Public License v1.0\n * which accompanies this distribution, and is available at\n * http://robocode.sourceforge.net/license/epl-v10.html\n */\npackage sample;\n\n\nimport robocode.HitByBulletEvent;\nimport robocode.Robot;\nimport robocode.ScannedRobotEvent;\n\nimport java.awt.*;\n\n\n/**\n * PaintingRobot - a sample robot that demonstrates the onPaint() and\n * getGraphics() methods.\n * Also demonstrate feature of debugging properties on RobotDialog\n * <p/>\n * Moves in a seesaw motion, and spins the gun around at each end.\n * When painting is enabled for this robot, a red circle will be painted\n * around this robot.\n *\n * @author Stefan Westen (original SGSample)\n * @author Pavel Savara (contributor)\n */\npublic class PaintingRobot extends Robot {\n\n	/**\n	 * PaintingRobot\'s run method - Seesaw\n	 */\n	public void run() {\n		while (true) {\n			ahead(100);\n			turnGunRight(360);\n			back(100);\n			turnGunRight(360);\n		}\n	}\n\n	/**\n	 * Fire when we see a robot\n	 */\n	public void onScannedRobot(ScannedRobotEvent e) {\n		// demonstrate feature of debugging properties on RobotDialog\n		setDebugProperty(\"lastScannedRobot\", e.getName()   \" at \"   e.getBearing()   \" degrees at time \"   getTime());\n		\n		fire(1);\n	}\n\n	/**\n	 * We were hit!  Turn perpendicular to the bullet,\n	 * so our seesaw might avoid a future shot.\n	 * In addition, draw orange circles where we were hit.\n	 */\n	public void onHitByBullet(HitByBulletEvent e) {\n		// demonstrate feature of debugging properties on RobotDialog\n		setDebugProperty(\"lastHitBy\", e.getName()   \" with power of bullet \"   e.getPower()   \" at time \"   getTime());\n\n		// show how to remove debugging property\n		setDebugProperty(\"lastScannedRobot\", null);\n\n		// gebugging by painting to battle view\n		Graphics2D g = getGraphics();\n\n		g.setColor(Color.orange);\n		g.drawOval((int) (getX() - 55), (int) (getY() - 55), 110, 110);\n		g.drawOval((int) (getX() - 56), (int) (getY() - 56), 112, 112);\n		g.drawOval((int) (getX() - 59), (int) (getY() - 59), 118, 118);\n		g.drawOval((int) (getX() - 60), (int) (getY() - 60), 120, 120);\n\n		turnLeft(90 - e.getBearing());\n	}\n\n	/**\n	 * Paint a red circle around our PaintingRobot\n	 */\n	public void onPaint(Graphics2D g) {\n		g.setColor(Color.red);\n		g.drawOval((int) (getX() - 50), (int) (getY() - 50), 100, 100);\n		g.setColor(new Color(0, 0xFF, 0, 30));\n		g.fillOval((int) (getX() - 60), (int) (getY() - 60), 120, 120);\n	}\n}\n',X'',0),
	(9,'User','sample','RamFire','Y','C:/robocode/robots/sample/RamFire.java','','Tue Jan 26 11:34:09 CST 2016','/**\n * Copyright (c) 2001-2014 Mathew A. Nelson and Robocode contributors\n * All rights reserved. This program and the accompanying materials\n * are made available under the terms of the Eclipse Public License v1.0\n * which accompanies this distribution, and is available at\n * http://robocode.sourceforge.net/license/epl-v10.html\n */\npackage sample;\n\n\nimport robocode.HitRobotEvent;\nimport robocode.Robot;\nimport robocode.ScannedRobotEvent;\n\nimport java.awt.*;\n\n\n/**\n * RamFire - a sample robot by Mathew Nelson.\n * <p/>\n * Drives at robots trying to ram them.\n * Fires when it hits them.\n *\n * @author Mathew A. Nelson (original)\n * @author Flemming N. Larsen (contributor)\n */\npublic class RamFire extends Robot {\n	int turnDirection = 1; // Clockwise or counterclockwise\n\n	/**\n	 * run: Spin around looking for a target\n	 */\n	public void run() {\n		// Set colors\n		setBodyColor(Color.lightGray);\n		setGunColor(Color.gray);\n		setRadarColor(Color.darkGray);\n\n		while (true) {\n			turnRight(5 * turnDirection);\n		}\n	}\n\n	/**\n	 * onScannedRobot:  We have a target.  Go get it.\n	 */\n	public void onScannedRobot(ScannedRobotEvent e) {\n\n		if (e.getBearing() >= 0) {\n			turnDirection = 1;\n		} else {\n			turnDirection = -1;\n		}\n\n		turnRight(e.getBearing());\n		ahead(e.getDistance());\n		scan(); // Might want to move ahead again!\n	}\n\n	/**\n	 * onHitRobot:  Turn to face robot, fire hard, and ram him again!\n	 */\n	public void onHitRobot(HitRobotEvent e) {\n		if (e.getBearing() >= 0) {\n			turnDirection = 1;\n		} else {\n			turnDirection = -1;\n		}\n		turnRight(e.getBearing());\n\n		// Determine a shot that won\'t kill the robot...\n		// We want to ram him instead for bonus points\n		if (e.getEnergy() > 16) {\n			fire(3);\n		} else if (e.getEnergy() > 10) {\n			fire(2);\n		} else if (e.getEnergy() > 4) {\n			fire(1);\n		} else if (e.getEnergy() > 2) {\n			fire(.5);\n		} else if (e.getEnergy() > .4) {\n			fire(.1);\n		}\n		ahead(40); // Ram him again!\n	}\n}\n\n\n\n\n',X'',0),
	(10,'User','sample','SittingDuck','Y','C:/robocode/robots/sample/SittingDuck.java','','Fri Jan 22 17:45:16 CST 2016','/**\n * Copyright (c) 2001-2014 Mathew A. Nelson and Robocode contributors\n * All rights reserved. This program and the accompanying materials\n * are made available under the terms of the Eclipse Public License v1.0\n * which accompanies this distribution, and is available at\n * http://robocode.sourceforge.net/license/epl-v10.html\n */\npackage sample;\n\n\nimport robocode.AdvancedRobot;\nimport robocode.RobocodeFileOutputStream;\n\nimport java.awt.*;\nimport java.io.BufferedReader;\nimport java.io.FileReader;\nimport java.io.IOException;\nimport java.io.PrintStream;\n\n\n/**\n * SittingDuck - a sample robot by Mathew Nelson.\n * <p/>\n * Along with sitting still doing nothing, this robot demonstrates persistency.\n *\n * @author Mathew A. Nelson (original)\n * @author Flemming N. Larsen (contributor)\n * @author Andrew Magargle (contributor)\n */\npublic class SittingDuck extends AdvancedRobot {\n	static boolean incrementedBattles = false;\n\n	public void run() {\n		setBodyColor(Color.yellow);\n		setGunColor(Color.yellow);\n\n		int roundCount, battleCount;\n\n		try {\n			BufferedReader reader = null;\n			try {\n				// Read file \"count.dat\" which contains 2 lines, a round count, and a battle count\n				reader = new BufferedReader(new FileReader(getDataFile(\"count.dat\")));\n\n				// Try to get the counts\n				roundCount = Integer.parseInt(reader.readLine());\n				battleCount = Integer.parseInt(reader.readLine());\n\n			} finally {\n				if (reader != null) {\n					reader.close();\n				}\n			}\n		} catch (IOException e) {\n			// Something went wrong reading the file, reset to 0.\n			roundCount = 0;\n			battleCount = 0;\n		} catch (NumberFormatException e) {\n			// Something went wrong converting to ints, reset to 0\n			roundCount = 0;\n			battleCount = 0;\n		}\n\n		// Increment the # of rounds\n		roundCount  ;\n\n		// If we haven\'t incremented # of battles already,\n		// Note: Because robots are only instantiated once per battle, member variables remain valid throughout it.\n		if (!incrementedBattles) {\n			// Increment # of battles\n			battleCount  ;\n			incrementedBattles = true;\n		}\n\n		PrintStream w = null;\n		try {\n			w = new PrintStream(new RobocodeFileOutputStream(getDataFile(\"count.dat\")));\n\n			w.println(roundCount);\n			w.println(battleCount);\n\n			// PrintStreams don\'t throw IOExceptions during prints, they simply set a flag.... so check it here.\n			if (w.checkError()) {\n				out.println(\"I could not write the count!\");\n			}\n		} catch (IOException e) {\n			out.println(\"IOException trying to write: \");\n			e.printStackTrace(out);\n		} finally {\n			if (w != null) {\n				w.close();\n			}\n		}\n		out.println(\"I have been a sitting duck for \"   roundCount   \" rounds, in \"   battleCount   \" battles.\"); \n	}\n}\n\n',X'',0),
	(11,'User','sample','SpinBot','Y','C:/robocode/robots/sample/SpinBot.java','','','/**\n * Copyright (c) 2001-2014 Mathew A. Nelson and Robocode contributors\n * All rights reserved. This program and the accompanying materials\n * are made available under the terms of the Eclipse Public License v1.0\n * which accompanies this distribution, and is available at\n * http://robocode.sourceforge.net/license/epl-v10.html\n */\npackage sample;\n\n\nimport robocode.AdvancedRobot;\nimport robocode.HitRobotEvent;\nimport robocode.ScannedRobotEvent;\n\nimport java.awt.*;\n\n\n/**\n * SpinBot - a sample robot by Mathew Nelson.\n * <p/>\n * Moves in a circle, firing hard when an enemy is detected.\n *\n * @author Mathew A. Nelson (original)\n * @author Flemming N. Larsen (contributor)\n */\npublic class SpinBot extends AdvancedRobot {\n\n	/**\n	 * SpinBot\'s run method - Circle\n	 */\n	public void run() {\n		// Set colors\n		setBodyColor(Color.blue);\n		setGunColor(Color.blue);\n		setRadarColor(Color.black);\n		setScanColor(Color.yellow);\n\n		// Loop forever\n		while (true) {\n			// Tell the game that when we take move,\n			// we\'ll also want to turn right... a lot.\n			setTurnRight(10000);\n			// Limit our speed to 5\n			setMaxVelocity(5);\n			// Start moving (and turning)\n			ahead(10000);\n			// Repeat.\n		}\n	}\n\n	/**\n	 * onScannedRobot: Fire hard!\n	 */\n	public void onScannedRobot(ScannedRobotEvent e) {\n		fire(3);\n	}\n\n	/**\n	 * onHitRobot:  If it\'s our fault, we\'ll stop turning and moving,\n	 * so we need to turn again to keep spinning.\n	 */\n	public void onHitRobot(HitRobotEvent e) {\n		if (e.getBearing() > -10 && e.getBearing() < 10) {\n			fire(3);\n		}\n		if (e.isMyFault()) {\n			turnRight(10);\n		}\n	}\n}',X'',0),
	(12,'User','sample','Target','Y','sample/Target.java','','Fri Jan 22 17:35:21 CST 2016','/**\n * Copyright (c) 2001-2014 Mathew A. Nelson and Robocode contributors\n * All rights reserved. This program and the accompanying materials\n * are made available under the terms of the Eclipse Public License v1.0\n * which accompanies this distribution, and is available at\n * http://robocode.sourceforge.net/license/epl-v10.html\n */\npackage sample;\n\n\nimport robocode.AdvancedRobot;\nimport robocode.Condition;\nimport robocode.CustomEvent;\n\nimport java.awt.*;\n\n\n/**\n * Target - a sample robot by Mathew Nelson.\n * <p/>\n * Sits still. Moves every time energy drops by 20.\n * This Robot demonstrates custom events.\n *\n * @author Mathew A. Nelson (original)\n * @author Flemming N. Larsen (contributor)\n */\npublic class Target extends AdvancedRobot {\n\n	int trigger; // Keeps track of when to move\n\n	/**\n	 * TrackFire\'s run method\n	 */\n	public void run() {\n		// Set colors\n		setBodyColor(Color.white);\n		setGunColor(Color.white);\n		setRadarColor(Color.white);\n\n		// Initially, we\'ll move when life hits 80\n		trigger = 80;\n		// Add a custom event named \"trigger hit\",\n		addCustomEvent(new Condition(\"triggerhit\") {\n			public boolean test() {\n				return (getEnergy() <= trigger);\n			}\n		});\n	}\n\n	/**\n	 * onCustomEvent handler\n	 */\n	public void onCustomEvent(CustomEvent e) {\n		// If our custom event \"triggerhit\" went off,\n		if (e.getCondition().getName().equals(\"triggerhit\")) {\n			// Adjust the trigger value, or\n			// else the event will fire again and again and again...\n			trigger -= 20;\n			out.println(\"Ouch, down to \"   (int) (getEnergy()   .5)   \" energy.\");\n			// move around a bit.\n			turnLeft(65);\n			ahead(100);\n		}\n	}\n}\n',X'',0),
	(13,'User','sample','Tracker','Y','sample/Tracker.java','','','/**\n * Copyright (c) 2001-2014 Mathew A. Nelson and Robocode contributors\n * All rights reserved. This program and the accompanying materials\n * are made available under the terms of the Eclipse Public License v1.0\n * which accompanies this distribution, and is available at\n * http://robocode.sourceforge.net/license/epl-v10.html\n */\npackage sample;\n\n\nimport robocode.HitRobotEvent;\nimport robocode.Robot;\nimport robocode.ScannedRobotEvent;\nimport robocode.WinEvent;\nimport static robocode.util.Utils.normalRelativeAngleDegrees;\n\nimport java.awt.*;\n\n\n/**\n * Tracker - a sample robot by Mathew Nelson.\n * <p/>\n * Locks onto a robot, moves close, fires when close.\n *\n * @author Mathew A. Nelson (original)\n * @author Flemming N. Larsen (contributor)\n */\npublic class Tracker extends Robot {\n	int count = 0; // Keeps track of how long we\'ve\n	// been searching for our target\n	double gunTurnAmt; // How much to turn our gun when searching\n	String trackName; // Name of the robot we\'re currently tracking\n\n	/**\n	 * run:  Tracker\'s main run function\n	 */\n	public void run() {\n		// Set colors\n		setBodyColor(new Color(128, 128, 50));\n		setGunColor(new Color(50, 50, 20));\n		setRadarColor(new Color(200, 200, 70));\n		setScanColor(Color.white);\n		setBulletColor(Color.blue);\n\n		// Prepare gun\n		trackName = null; // Initialize to not tracking anyone\n		setAdjustGunForRobotTurn(true); // Keep the gun still when we turn\n		gunTurnAmt = 10; // Initialize gunTurn to 10\n\n		// Loop forever\n		while (true) {\n			// turn the Gun (looks for enemy)\n			turnGunRight(gunTurnAmt);\n			// Keep track of how long we\'ve been looking\n			count++;\n			// If we\'ve haven\'t seen our target for 2 turns, look left\n			if (count > 2) {\n				gunTurnAmt = -10;\n			}\n			// If we still haven\'t seen our target for 5 turns, look right\n			if (count > 5) {\n				gunTurnAmt = 10;\n			}\n			// If we *still* haven\'t seen our target after 10 turns, find another target\n			if (count > 11) {\n				trackName = null;\n			}\n		}\n	}\n\n	/**\n	 * onScannedRobot:  Here\'s the good stuff\n	 */\n	public void onScannedRobot(ScannedRobotEvent e) {\n\n		// If we have a target, and this isn\'t it, return immediately\n		// so we can get more ScannedRobotEvents.\n		if (trackName != null && !e.getName().equals(trackName)) {\n			return;\n		}\n\n		// If we don\'t have a target, well, now we do!\n		if (trackName == null) {\n			trackName = e.getName();\n			out.println(\"Tracking \" + trackName);\n		}\n		// This is our target.  Reset count (see the run method)\n		count = 0;\n		// If our target is too far away, turn and move toward it.\n		if (e.getDistance() > 150) {\n			gunTurnAmt = normalRelativeAngleDegrees(e.getBearing() + (getHeading() - getRadarHeading()));\n\n			turnGunRight(gunTurnAmt); // Try changing these to setTurnGunRight,\n			turnRight(e.getBearing()); // and see how much Tracker improves...\n			// (you\'ll have to make Tracker an AdvancedRobot)\n			ahead(e.getDistance() - 140);\n			return;\n		}\n\n		// Our target is close.\n		gunTurnAmt = normalRelativeAngleDegrees(e.getBearing() + (getHeading() - getRadarHeading()));\n		turnGunRight(gunTurnAmt);\n		fire(3);\n\n		// Our target is too close!  Back up.\n		if (e.getDistance() < 100) {\n			if (e.getBearing() > -90 && e.getBearing() <= 90) {\n				back(40);\n			} else {\n				ahead(40);\n			}\n		}\n		scan();\n	}\n\n	/**\n	 * onHitRobot:  Set him as our new target\n	 */\n	public void onHitRobot(HitRobotEvent e) {\n		// Only print if he\'s not already our target.\n		if (trackName != null && !trackName.equals(e.getName())) {\n			out.println(\"Tracking \" + e.getName() + \" due to collision\");\n		}\n		// Set the target\n		trackName = e.getName();\n		// Back up a bit.\n		// Note:  We won\'t get scan events while we\'re doing this!\n		// An AdvancedRobot might use setBack(); execute();\n		gunTurnAmt = normalRelativeAngleDegrees(e.getBearing() + (getHeading() - getRadarHeading()));\n		turnGunRight(gunTurnAmt);\n		fire(3);\n		back(50);\n	}\n\n	/**\n	 * onWin:  Do a victory dance\n	 */\n	public void onWin(WinEvent e) {\n		for (int i = 0; i < 50; i++) {\n			turnRight(30);\n			turnLeft(30);\n		}\n	}\n}',X'',0),
	(14,'User','sample','TrackFire','Y','sample/TrackFire.java','','','/**\n * Copyright (c) 2001-2014 Mathew A. Nelson and Robocode contributors\n * All rights reserved. This program and the accompanying materials\n * are made available under the terms of the Eclipse Public License v1.0\n * which accompanies this distribution, and is available at\n * http://robocode.sourceforge.net/license/epl-v10.html\n */\npackage sample;\n\n\nimport robocode.Robot;\nimport robocode.ScannedRobotEvent;\nimport robocode.WinEvent;\nimport static robocode.util.Utils.normalRelativeAngleDegrees;\n\nimport java.awt.*;\n\n\n/**\n * TrackFire - a sample robot by Mathew Nelson.\n * <p/>\n * Sits still. Tracks and fires at the nearest robot it sees.\n *\n * @author Mathew A. Nelson (original)\n * @author Flemming N. Larsen (contributor)\n */\npublic class TrackFire extends Robot {\n\n	/**\n	 * TrackFire\'s run method\n	 */\n	public void run() {\n		// Set colors\n		setBodyColor(Color.pink);\n		setGunColor(Color.pink);\n		setRadarColor(Color.pink);\n		setScanColor(Color.pink);\n		setBulletColor(Color.pink);\n\n		// Loop forever\n		while (true) {\n			turnGunRight(10); // Scans automatically\n		}\n	}\n\n	/**\n	 * onScannedRobot:  We have a target.  Go get it.\n	 */\n	public void onScannedRobot(ScannedRobotEvent e) {\n		// Calculate exact location of the robot\n		double absoluteBearing = getHeading() + e.getBearing();\n		double bearingFromGun = normalRelativeAngleDegrees(absoluteBearing - getGunHeading());\n\n		// If it\'s close enough, fire!\n		if (Math.abs(bearingFromGun) <= 3) {\n			turnGunRight(bearingFromGun);\n			// We check gun heat here, because calling fire()\n			// uses a turn, which could cause us to lose track\n			// of the other robot.\n			if (getGunHeat() == 0) {\n				fire(Math.min(3 - Math.abs(bearingFromGun), getEnergy() - .1));\n			}\n		} // otherwise just set the gun to turn.\n		// Note:  This will have no effect until we call scan()\n		else {\n			turnGunRight(bearingFromGun);\n		}\n		// Generates another scan event if we see a robot.\n		// We only need to call this if the gun (and therefore radar)\n		// are not turning.  Otherwise, scan is called automatically.\n		if (bearingFromGun == 0) {\n			scan();\n		}\n	}\n\n	public void onWin(WinEvent e) {\n		// Victory dance\n		turnRight(36000);\n	}\n}',X'',0),
	(15,'User','sample','VelociRobot','Y','C:/robocode/robots/sample/VelociRobot.java','','','/**\n * Copyright (c) 2001-2014 Mathew A. Nelson and Robocode contributors\n * All rights reserved. This program and the accompanying materials\n * are made available under the terms of the Eclipse Public License v1.0\n * which accompanies this distribution, and is available at\n * http://robocode.sourceforge.net/license/epl-v10.html\n */\npackage sample;\n\n\nimport robocode.HitByBulletEvent;\nimport robocode.HitWallEvent;\nimport robocode.RateControlRobot;\nimport robocode.ScannedRobotEvent;\n\n\n/**\n * This is a sample of a robot using the RateControlRobot class\n * \n * @author Joshua Galecki (original)\n */\npublic class VelociRobot extends RateControlRobot {\n\n	int turnCounter;\n	public void run() {\n\n		turnCounter = 0;\n		setGunRotationRate(15);\n		\n		while (true) {\n			if (turnCounter % 64 == 0) {\n				// Straighten out, if we were hit by a bullet and are turning\n				setTurnRate(0);\n				// Go forward with a velocity of 4\n				setVelocityRate(4);\n			}\n			if (turnCounter % 64 == 32) {\n				// Go backwards, faster\n				setVelocityRate(-6);\n			}\n			turnCounter++;\n			execute();\n		}\n	}\n\n	public void onScannedRobot(ScannedRobotEvent e) {\n		fire(1);\n	}\n\n	public void onHitByBullet(HitByBulletEvent e) {\n		// Turn to confuse the other robot\n		setTurnRate(5);\n	}\n	\n	public void onHitWall(HitWallEvent e) {\n		// Move away from the wall\n		setVelocityRate(-1 * getVelocityRate());\n	}\n}',X'',0),
	(16,'User','sample','Walls','Y','sample/Walls.java','','Mon Jan 25 10:03:39 CST 2016','/**\n * Copyright (c) 2001-2014 Mathew A. Nelson and Robocode contributors\n * All rights reserved. This program and the accompanying materials\n * are made available under the terms of the Eclipse Public License v1.0\n * which accompanies this distribution, and is available at\n * http://robocode.sourceforge.net/license/epl-v10.html\n */\npackage sample;\n\n\nimport robocode.HitRobotEvent;\nimport robocode.Robot;\nimport robocode.ScannedRobotEvent;\n\nimport java.awt.*;\n\n\n/**\n * Walls - a sample robot by Mathew Nelson, and maintained by Flemming N. Larsen\n * <p/>\n * Moves around the outer edge with the gun facing in.\n *\n * @author Mathew A. Nelson (original)\n * @author Flemming N. Larsen (contributor)\n */\npublic class Walls extends Robot {\n\n	boolean peek; // Don\'t turn if there\'s a robot there\n	double moveAmount; // How much to move\n\n	/**\n	 * run: Move around the walls\n	 */\n	public void run() {\n		// Set colors\n		setBodyColor(Color.black);\n		setGunColor(Color.black);\n		setRadarColor(Color.orange);\n		setBulletColor(Color.cyan);\n		setScanColor(Color.cyan);\n\n		// Initialize moveAmount to the maximum possible for this battlefield.\n		moveAmount = Math.max(getBattleFieldWidth(), getBattleFieldHeight());\n		// Initialize peek to false\n		peek = false;\n\n		// turnLeft to face a wall.\n		// getHeading() % 90 means the remainder of\n		// getHeading() divided by 90.\n		turnLeft(getHeading() % 90);\n		ahead(moveAmount);\n		// Turn the gun to turn right 90 degrees.\n		peek = true;\n		turnGunRight(90);\n		turnRight(90);\n\n		while (true) {\n			// Look before we turn when ahead() completes.\n			peek = true;\n			// Move up the wall\n			ahead(moveAmount);\n			// Don\'t look now\n			peek = false;\n			// Turn to the next wall\n			turnRight(90);\n		}\n	}\n\n	/**\n	 * onHitRobot:  Move away a bit.\n	 */\n	public void onHitRobot(HitRobotEvent e) {\n		// If he\'s in front of us, set back up a bit.\n		if (e.getBearing() > -90 && e.getBearing() < 90) {\n			back(100);\n		} // else he\'s in back of us, so set ahead a bit.\n		else {\n			ahead(100);\n		}\n	}\n\n	/**\n	 * onScannedRobot:  Fire!\n	 */\n	public void onScannedRobot(ScannedRobotEvent e) {\n		fire(2);\n		// Note that scan is called automatically when the robot is moving.\n		// By calling it manually here, we make sure we generate another scan event if there\'s a robot on the next\n		// wall, so that we do not start moving up it until it\'s gone.\n		if (peek) {\n			scan();\n		}\n	}\n}\n',X'',0),
	(17,'User','sampleex','Alien','Y','C:/robocode/robots/sampleex/Alien.java','','','/**\n * Copyright (c) 2001-2014 Mathew A. Nelson and Robocode contributors\n * All rights reserved. This program and the accompanying materials\n * are made available under the terms of the Eclipse Public License v1.0\n * which accompanies this distribution, and is available at\n * http://robocode.sourceforge.net/license/epl-v10.html\n */\npackage sampleex;\n\n\nimport robocode.*;\nimport robocode.robotinterfaces.IBasicEvents;\nimport robocode.robotinterfaces.IBasicRobot;\nimport robocode.robotinterfaces.peer.IBasicRobotPeer;\nimport robocode.robotinterfaces.peer.IStandardRobotPeer;\n\nimport java.io.PrintStream;\n\n\n/**\n * A sample robot.\n * Is not inherited from classic base robots, uses new experimental access to RobotPeer.\n * Use -DEXPERIMENTAL=true to start robocode for this robot.\n *\n * @author Pavel Savara (original)\n */\npublic class Alien implements IBasicEvents, IBasicRobot, Runnable {\n\n	PrintStream out;\n	IStandardRobotPeer peer;\n\n	public Runnable getRobotRunnable() {\n		return this;\n	}\n\n	public IBasicEvents getBasicEventListener() {\n		return this;\n	}\n\n	public void setPeer(IBasicRobotPeer iRobotPeer) {\n		peer = (IStandardRobotPeer) iRobotPeer;\n	}\n\n	public void setOut(PrintStream printStream) {\n		out = printStream;\n	}\n\n	public void run() {\n		while (true) {\n			peer.move(100); // Move ahead 100\n			peer.turnGun(Math.PI * 2); // Spin gun around\n			peer.move(-100); // Move back 100\n			peer.turnGun(Math.PI * 2); // Spin gun around\n		}\n	}\n\n	public void onScannedRobot(ScannedRobotEvent e) {\n		peer.setFire(1);\n	}\n\n	public void onHitByBullet(HitByBulletEvent e) {\n		peer.turnBody(Math.PI / 2 + e.getBearingRadians());\n	}\n\n	public void onStatus(StatusEvent e) {}\n\n	public void onBulletHit(BulletHitEvent e) {}\n\n	public void onBulletHitBullet(BulletHitBulletEvent e) {}\n\n	public void onBulletMissed(BulletMissedEvent e) {}\n\n	public void onDeath(DeathEvent e) {}\n\n	public void onHitRobot(HitRobotEvent e) {}\n\n	public void onHitWall(HitWallEvent e) {}\n\n	public void onRobotDeath(RobotDeathEvent e) {}\n\n	public void onWin(WinEvent e) {}\n}',X'',0),
