IBasicEvents {\n		public void onScannedRobot(ScannedRobotEvent e) {\n			peer.setFire(1);\n		}\n\n		public void onHitByBullet(HitByBulletEvent e) {\n			peer.turnBody(Math.PI / 2   e.getBearingRadians());\n		}\n\n		public void onStatus(StatusEvent e) {}\n\n		public void onBulletHit(BulletHitEvent e) {}\n\n		public void onBulletHitBullet(BulletHitBulletEvent e) {}\n\n		public void onBulletMissed(BulletMissedEvent e) {}\n\n		public void onDeath(DeathEvent e) {}\n\n		public void onHitRobot(HitRobotEvent e) {}\n\n		public void onHitWall(HitWallEvent e) {}\n\n		public void onRobotDeath(RobotDeathEvent e) {}\n\n		public void onWin(WinEvent e) {}\n	}\n}',X'',0),
	(19,'User','sampleex','MasterAndSlave','Y','C:/robocode/robots/sampleex/MasterAndSlave.java','','','/**\n * Copyright (c) 2001-2014 Mathew A. Nelson and Robocode contributors\n * All rights reserved. This program and the accompanying materials\n * are made available under the terms of the Eclipse Public License v1.0\n * which accompanies this distribution, and is available at\n * http://robocode.sourceforge.net/license/epl-v10.html\n */\npackage sampleex;\n\n\nimport robocode.AdvancedRobot;\nimport robocode.HitByBulletEvent;\nimport robocode.ScannedRobotEvent;\nimport robocode.robotinterfaces.IAdvancedEvents;\nimport robocode.robotinterfaces.IAdvancedRobot;\nimport robocode.robotinterfaces.IBasicEvents;\nimport robocode.robotinterfaces.IInteractiveEvents;\nimport robocode.robotinterfaces.peer.IBasicRobotPeer;\n\nimport java.io.PrintStream;\n\n\n/**\n * @author Pavel Savara (original)\n */\npublic class MasterAndSlave extends MasterBase implements IAdvancedRobot {\n\n	/**\n	 * This is not showing any aditional qualities over normal MyFirst robot.\n	 * But it could, because architecture is no more tied by inheritance from Robot base class.\n	 */\n	public void run() {\n		while (true) {\n			ahead(100); // Move ahead 100\n			turnGunRight(360); // Spin gun around\n			back(100); // Move back 100\n			turnGunRight(360); // Spin gun around\n		}\n	}\n\n	public void onScannedRobot(ScannedRobotEvent e) {\n		fire(1);\n	}\n\n	public void onHitByBullet(HitByBulletEvent e) {\n		turnLeft(90 - e.getBearing());\n	}\n\n	public IInteractiveEvents getInteractiveEventListener() {\n		return null;\n	}\n}\n\n\n/**\n * Infrastructure base class, for helpers and boring implementation details\n */\nabstract class MasterBase {\n\n	public MasterBase() {\n		helperRobot = new Slave(this);\n	}\n\n	private final AdvancedRobot helperRobot;\n\n	public IAdvancedEvents getAdvancedEventListener() {\n		return helperRobot;\n	}\n\n	public IInteractiveEvents getSystemEventListener() {\n		return helperRobot;\n	}\n\n	public Runnable getRobotRunnable() {\n		return helperRobot;\n	}\n\n	public IBasicEvents getBasicEventListener() {\n		return helperRobot;\n	}\n\n	public void setPeer(IBasicRobotPeer robotPeer) {\n		helperRobot.setPeer(robotPeer);\n	}\n\n	public void setOut(PrintStream printStream) {\n		helperRobot.setOut(printStream);\n	}\n\n	public void turnGunRight(double degrees) {\n		helperRobot.turnGunRight(degrees);\n	}\n\n	public void turnLeft(double degrees) {\n		helperRobot.turnLeft(degrees);\n	}\n\n	public void ahead(double distance) {\n		helperRobot.ahead(distance);\n	}\n\n	public void back(double distance) {\n		helperRobot.back(distance);\n	}\n\n	public void fire(double power) {\n		helperRobot.fire(power);\n	}\n\n	public void onScannedRobot(ScannedRobotEvent e) {}\n\n	public void onHitByBullet(HitByBulletEvent e) {}\n\n	public void run() {}\n}',X'',0),
	(20,'User','sampleex','ProxyOfGreyEminence','Y','C:/robocode/robots/sampleex/ProxyOfGreyEminence.java','','','/**\n * Copyright (c) 2001-2014 Mathew A. Nelson and Robocode contributors\n * All rights reserved. This program and the accompanying materials\n * are made available under the terms of the Eclipse Public License v1.0\n * which accompanies this distribution, and is available at\n * http://robocode.sourceforge.net/license/epl-v10.html\n */\npackage sampleex;\n\n\nimport robocode.AdvancedRobot;\nimport robocode.HitByBulletEvent;\nimport robocode.ScannedRobotEvent;\n\n\n/**\n * This is just dummy proxy, it\'s hiding the Eminence and\n * giving it more freedom to inherit from Monk rather than from Robot.\n *\n * @author Pavel Savara (original)\n */\npublic class ProxyOfGreyEminence extends AdvancedRobot {\n	private final GreyEminence monk;\n\n	public ProxyOfGreyEminence() {\n		monk = new GreyEminence(this);\n	}\n\n	public void onHitByBullet(HitByBulletEvent event) {\n		monk.onHitByBullet(event);\n	}\n\n	public void onScannedRobot(ScannedRobotEvent event) {\n		monk.onScannedRobot(event);\n	}\n\n	public void run() {\n		monk.run();\n	}\n}\n\n\n/**\n * The power behind the throne.\n */\nclass GreyEminence extends RegullarMonk {\n	private final ProxyOfGreyEminence proxy;\n\n	public GreyEminence(ProxyOfGreyEminence proxy) {\n		this.proxy = proxy;\n	}\n\n	/**\n	 * This is not showing any aditional qualities over normal MyFirst robot.\n	 * But it could, because architecture is no more tied by inheritance from Robot base class.\n	 */\n	public void run() {\n		while (true) {\n			proxy.ahead(100); // Move ahead 100\n			proxy.turnGunRight(360); // Spin gun around\n			proxy.back(100); // Move back 100\n			proxy.turnGunRight(360); // Spin gun around\n		}\n	}\n\n	public void onScannedRobot(ScannedRobotEvent e) {\n		proxy.fire(1);\n	}\n\n	public void onHitByBullet(HitByBulletEvent e) {\n		proxy.turnLeft(90 - e.getBearing());\n	}\n}',X'',0),
	(21,'User','sampleex','RegullarMonk','Y','C:/robocode/robots/sampleex/RegullarMonk.java','','','/**\n * Copyright (c) 2001-2014 Mathew A. Nelson and Robocode contributors\n * All rights reserved. This program and the accompanying materials\n * are made available under the terms of the Eclipse Public License v1.0\n * which accompanies this distribution, and is available at\n * http://robocode.sourceforge.net/license/epl-v10.html\n */\npackage sampleex;\n\n\n/**\n * Monk of a order. Implements anything too boring for Eminence.\n * The infractructure base class.\n *\n * @author Pavel Savara (original)\n */\nabstract class RegullarMonk {}',X'',0),
	(22,'User','sampleex','Slave','Y','C:/robocode/robots/sampleex/Slave.java','','','/**\n * Copyright (c) 2001-2014 Mathew A. Nelson and Robocode contributors\n * All rights reserved. This program and the accompanying materials\n * are made available under the terms of the Eclipse Public License v1.0\n * which accompanies this distribution, and is available at\n * http://robocode.sourceforge.net/license/epl-v10.html\n */\npackage sampleex;\n\n\nimport robocode.AdvancedRobot;\nimport robocode.HitByBulletEvent;\nimport robocode.ScannedRobotEvent;\n\n\n/**\n * This is robot derived from AdvancedRobot.\n * Only reason to use this inheritance and this class is that external robots are unable to call RobotPeer directly.\n *\n * @author Pavel Savara (original)\n */\nclass Slave extends AdvancedRobot {\n	final MasterBase parent;\n\n	public Slave(MasterBase parent) {\n		this.parent = parent;\n	}\n\n	public void run() {\n		parent.run();\n	}\n\n	public void onScannedRobot(ScannedRobotEvent e) {\n		parent.onScannedRobot(e);\n	}\n\n	public void onHitByBullet(HitByBulletEvent e) {\n		parent.onHitByBullet(e);\n	}\n}',X'',0),
	(23,'User','samplesentry','BorderGuard','Y','C:/robocode/robots/samplesentry/BorderGuard','','Mon Jan 25 10:04:31 CST 2016','/**\n * Copyright (c) 2001-2014 Mathew A. Nelson and Robocode contributors\n * All rights reserved. This program and the accompanying materials\n * are made available under the terms of the Eclipse Public License v1.0\n * which accompanies this distribution, and is available at\n * http://robocode.sourceforge.net/license/epl-v10.html\n */\npackage samplesentry;\n\n\nimport robocode.*;\nimport robocode.util.Utils;\n\nimport java.awt.BasicStroke;\nimport java.awt.Color;\nimport java.awt.Graphics2D;\nimport java.awt.geom.Point2D;\nimport java.util.*;\n\n\n/**\n * BorderGuard - is a sample robot that demonstrates how the BorderSentry interface can be used for\n * creating a robot that guards the border area of the battle field, and which is efficient against\n * robots trying to hide at corners and robots sneaking around near the borders.\n * <p>\n * This robot is somewhat advanced due to:<br>\n * 1) it uses linear targeting for predicting how to hit robots that moves in straight lines.<br>\n * 2) it will only fire at a robot, if it figures out that its bullets will do damage to that\n * particular robot, e.g. when the robots predicted future position will be within the sentry border\n * attack range.<br>\n * 3) it has an efficient scanner that keeps the scan angle as little as possible to get as new\n * scanned data for enemy robots as possible.<br>\n * 4) it picks a target robot to fire at, which is the nearest robot our robot will be able to\n * damage (by predicting its future position using linear targeting).<br>\n * 5) it only moves along the borders of the battle field and constantly changes its direction, so\n * it is not an easy target, and it will try to get as close to its target robot as possible.<br>\n * <p>\n * Lots of improvements can be made to this robot by copying it - making it even stronger. The\n * intention with of this sample robot is to serve as a more advanced example of how a AdvancedRobot\n * can be made, and how it can be structured as most sample robots are far simpler.\n * <p>\n * \n * Credits goes to the hard working Robocoders at the RoboWiki. :-)\n * <p>\n * \n * This robot makes use of the Oldest Scanned melee scanner from the RoboWiki:<br>\n * http://robowiki.net/wiki/Melee_Radar\n * <p>\n * \n * In addition, it makes use of the Exact Non-iterative Solution for Linear Targeting from the\n * RoboWiki:<br>\n * http://robowiki.net/wiki/Linear_Targeting\n * \n * @author Flemming N. Larsen\n * \n * @version 1.0\n * \n * @since 1.9.0.0\n */\npublic class BorderGuard extends AdvancedRobot implements BorderSentry {\n\n	// Constants\n	final double FIREPOWER = 3; // Max. power => violent as this robot can afford it!\n	final double HALF_ROBOT_SIZE = 18; // Robot size is 36x36 units, so the half size is 18 units\n\n	// Map containing data for all scanned robots.\n	// The key to the map is a robot name and the value is an object containing robot data.\n	final Map<String, RobotData> enemyMap;\n\n	// Scanning direction, where the radar turns to the right with positive values, and turns\n	// to the left with negative values.\n	double scanDir = 1;\n\n	// Oldest scanned robot. Can be null.\n	RobotData oldestScanned;\n\n	// Target robot for the gun. Can be null meaning that there is currently no target robot.\n	RobotData target;\n\n	// Last time when the robot shifted its direction\n	long lastDirectionShift;\n\n	// Current direction, where 1 means ahead (forward) and -1 means back\n	int direction = 1;\n\n	/**\n	 * Constructs this robot.\n	 */\n	public BorderGuard() {\n		// We initialize a specialized HashMap that uses a linked list for the access order.\n		// This means that the last accessed robot entry is listed first, when we iterate over its\n		// values. This robot always sweep the radar towards the oldest scanned robot.\n		enemyMap = new LinkedHashMap<String, RobotData>(5, 2, true);\n	}\n\n	/**\n	 * Main method that is called by the game when the robot engage in the next round of a battle.\n	 */\n	@Override\n	public void run() {\n		// Do initialization stuff here before the loop\n		initialize();\n\n		// Loop forever. If the robot does not take action, the game will disable our robot!\n		while (true) {\n			// Handle a single turn...\n\n			// Handle the radar that scans enemy robots\n			handleRadar();\n			// Handle the gun by turning it and fire at our target\n			handleGun();\n			// Move the robot around on the battlefield\n			moveRobot();\n\n			// Scan for other robots. Note that this method will execute all pending commands for\n			// the next turn. Hence, scan() ends the turn for our robot.\n			scan();\n		}\n	}\n\n	/**\n	 * This method is called by the game when your robot sees another robot, i.e. when the robot\'s\n	 * radar scan \"hits\" another robot.\n	 * \n	 * @param scannedRobotEvent\n	 *            is a ScannedRobotEvent event.\n	 */\n	@Override\n	public void onScannedRobot(ScannedRobotEvent scannedRobotEvent) {\n		// Check that the scanned robot is not a sentry robot\n		if (!scannedRobotEvent.isSentryRobot()) {\n			// The scanned robot is not a sentry robot...\n\n			// Update the enemy map\n			updateEnemyMap(scannedRobotEvent);\n\n			// Update the scan direction\n			updateScanDirection(scannedRobotEvent);\n\n			// Update enemy target positions\n			updateEnemyTargetPositions();\n		}\n	}\n\n	/**\n	 * This method is called by the game when another robot dies.\n	 * \n	 * @param robotDeathEvent\n	 *            is the RobotDeathEvent that occurs, when another robot dies, which contains data\n	 *            for the robot that died.\n	 */\n	@Override\n	public void onRobotDeath(RobotDeathEvent robotDeathEvent) {\n		// Gets the name of the robot that died\n		final String deadRobotName = robotDeathEvent.getName();\n\n		// Remove the robot data for the robot that died from the enemy map\n		enemyMap.remove(deadRobotName);\n\n		// Remove the data entry for the oldest scanned robot, if we have such an entry\n		if (oldestScanned != null ',X'',0),
	(24,'User','sampleteam','MyFirstDroid','Y','C:/robocode/robots/sampleteam/MyFirstDroid.java','','','/**\n * Copyright (c) 2001-2014 Mathew A. Nelson and Robocode contributors\n * All rights reserved. This program and the accompanying materials\n * are made available under the terms of the Eclipse Public License v1.0\n * which accompanies this distribution, and is available at\n * http://robocode.sourceforge.net/license/epl-v10.html\n */\npackage sampleteam;\n\n\nimport robocode.Droid;\nimport robocode.MessageEvent;\nimport robocode.TeamRobot;\nimport static robocode.util.Utils.normalRelativeAngleDegrees;\n\n\n/**\n * SimpleDroid - a sample robot by Mathew Nelson.\n * <p/>\n * Follows orders of team leader.\n *\n * @author Mathew A. Nelson (original)\n * @author Flemming N. Larsen (contributor)\n */\npublic class MyFirstDroid extends TeamRobot implements Droid {\n\n	/**\n	 * run:  Droid\'s default behavior\n	 */\n	public void run() {\n		out.println(\"MyFirstDroid ready.\");\n	}\n\n	/**\n	 * onMessageReceived:  What to do when our leader sends a message\n	 */\n	public void onMessageReceived(MessageEvent e) {\n		// Fire at a point\n		if (e.getMessage() instanceof Point) {\n			Point p = (Point) e.getMessage();\n			// Calculate x and y to target\n			double dx = p.getX() - this.getX();\n			double dy = p.getY() - this.getY();\n			// Calculate angle to target\n			double theta = Math.toDegrees(Math.atan2(dx, dy));\n\n			// Turn gun to target\n			turnGunRight(normalRelativeAngleDegrees(theta - getGunHeading()));\n			// Fire hard!\n			fire(3);\n		} // Set our colors\n		else if (e.getMessage() instanceof RobotColors) {\n			RobotColors c = (RobotColors) e.getMessage();\n\n			setBodyColor(c.bodyColor);\n			setGunColor(c.gunColor);\n			setRadarColor(c.radarColor);\n			setScanColor(c.scanColor);\n			setBulletColor(c.bulletColor);\n		}\n	}\n}',X'',0),
	(25,'User','sampleteam','MyFirstLeader','Y','C:/robocode/robots/sampleteam/MyFirstTeam.java','','','/**\n * Copyright (c) 2001-2014 Mathew A. Nelson and Robocode contributors\n * All rights reserved. This program and the accompanying materials\n * are made available under the terms of the Eclipse Public License v1.0\n * which accompanies this distribution, and is available at\n * http://robocode.sourceforge.net/license/epl-v10.html\n */\npackage sampleteam;\n\n\nimport robocode.HitByBulletEvent;\nimport robocode.ScannedRobotEvent;\nimport robocode.TeamRobot;\n\nimport java.awt.*;\nimport java.io.IOException;\n\n\n/**\n * MyFirstLeader - a sample team robot by Mathew Nelson.\n * <p/>\n * Looks around for enemies, and orders teammates to fire\n *\n * @author Mathew A. Nelson (original)\n * @author Flemming N. Larsen (contributor)\n */\npublic class MyFirstLeader extends TeamRobot {\n\n	/**\n	 * run:  Leader\'s default behavior\n	 */\n	public void run() {\n		// Prepare RobotColors object\n		RobotColors c = new RobotColors();\n\n		c.bodyColor = Color.red;\n		c.gunColor = Color.red;\n		c.radarColor = Color.red;\n		c.scanColor = Color.yellow;\n		c.bulletColor = Color.yellow;\n\n		// Set the color of this robot containing the RobotColors\n		setBodyColor(c.bodyColor);\n		setGunColor(c.gunColor);\n		setRadarColor(c.radarColor);\n		setScanColor(c.scanColor);\n		setBulletColor(c.bulletColor);\n		try {\n			// Send RobotColors object to our entire team\n			broadcastMessage(c);\n		} catch (IOException ignored) {}\n		// Normal behavior\n		while (true) {\n			setTurnRadarRight(10000);\n			ahead(100);\n			back(100);\n		}\n	}\n\n	/**\n	 * onScannedRobot:  What to do when you see another robot\n	 */\n	public void onScannedRobot(ScannedRobotEvent e) {\n		// Don\'t fire on teammates\n		if (isTeammate(e.getName())) {\n			return;\n		}\n		// Calculate enemy bearing\n		double enemyBearing = this.getHeading() + e.getBearing();\n		// Calculate enemy\'s position\n		double enemyX = getX() + e.getDistance() * Math.sin(Math.toRadians(enemyBearing));\n		double enemyY = getY() + e.getDistance() * Math.cos(Math.toRadians(enemyBearing));\n\n		try {\n			// Send enemy position to teammates\n			broadcastMessage(new Point(enemyX, enemyY));\n		} catch (IOException ex) {\n			out.println(\"Unable to send order: \");\n			ex.printStackTrace(out);\n		}\n	}\n\n	/**\n	 * onHitByBullet:  Turn perpendicular to bullet path\n	 */\n	public void onHitByBullet(HitByBulletEvent e) {\n		turnLeft(90 - e.getBearing());\n	}\n}',X'',0),
	(26,'User','sampleteam','Point','Y','C:/robocode/robots/sampleteam/MyFirstLeader.java','','','/**\n * Copyright (c) 2001-2014 Mathew A. Nelson and Robocode contributors\n * All rights reserved. This program and the accompanying materials\n * are made available under the terms of the Eclipse Public License v1.0\n * which accompanies this distribution, and is available at\n * http://robocode.sourceforge.net/license/epl-v10.html\n */\npackage sampleteam;\n\n\n/**\n * Point - a serializable point class\n */\npublic class Point implements java.io.Serializable {\n\n	private static final long serialVersionUID = 1L;\n\n	private double x = 0.0;\n	private double y = 0.0;\n\n	public Point(double x, double y) {\n		this.x = x;\n		this.y = y;\n	}\n\n	public double getX() {\n		return x;\n	}\n\n	public double getY() {\n		return y;\n	}\n}',X'',0),
